package buildingMapsimport atomicTest.eqfun main() {    val map = mapOf(        1 to "one",        2 to "two", 3 to "three", 4 to "four"    )    map.filterKeys { it % 2 == 1 } eq            mapOf(1 to "one", 3 to "three")    map.filterValues { it.contains('o') } eq            mapOf(                1 to "one",                2 to "two",                4 to "four"            )    map.filter {entry ->        entry.key % 2 == 1 &&                entry.value.contains('o')    } eq mapOf(1 to "one")}