package buildingMapsimport atomicTest.eqfun main() {    val groups =        people().groupBy { it.name.first() }    // groupBy()는 빠르게 맵을 만듬    groups['A'] eq listOf(        Person("Alice", 21),        Person("Arthricia", 15)    )    groups['Z'] eq null    people().filter {        it.name.first() == 'A'    } eq listOf(        Person("Alice", 21),        Person("Arthricia", 15)    )    people().filter {        it.name.first() == 'F'    } eq listOf(Person("Franz", 21))    // 그룹이 2개로 나눠지는 경우에는 partition이 적합하다.    // 그룹이 3개 이상으로 나눠지는 경우에는 groupBy가 적합하다.    people().partition {        it.name.first() == 'A'    } eq Pair(        listOf(            Person("Alice", 21),            Person("Arthricia", 15)        ), listOf(            Person("Bob", 25),            Person("Bill", 25),            Person("Birdperson", 42),            Person("Charlie", 21),            Person("Crocubot", 42),            Person("Franz", 21),            Person("Revolio", 33)))}