package dataTypefun main() {    val whole: Int = 11    val fractional: Double = 1.4    val trueOrFalse: Boolean = true    val words: String = "A Value"    val character: Char = 'Z'    val lines: String = """       쌍따옴표 3개는 String을        다양한 줄을 사용해서        작성할 수 있다.    """    println("whole = ${whole}")    println("fractional = ${fractional}")    println("trueOrFalse = ${trueOrFalse}")    println("words = ${words}")    println("character = ${character}")    println("line = ${lines}")}