package destructuringimport atomicTest.eqimport kotlin.collections.listOf as listOfdata class Tuple(    val i: Int,    val d: Double,    val s: String,    val b: Boolean,    val l: List<Int>)fun main() {    val tuple = Tuple(        1, 3.14, "Mouse", false, listOf()    )    val (i, d, s, b, l) = tuple;    i eq 1    d eq 3.14    s eq "Mouse"    b eq false    l eq listOf()    // 식별자 중 일부가 필요하지 않은 경우,    // 이름 대신 밑줄로 사용 가능하고,    // 맨 뒤쪽의 이름들은 아예 생략 가능하다.    val (_, _, animal) = tuple    animal eq "Mouse"}