package enumerationsimport atomicTest.traceimport enumerations.Level.*fun checkLevel(level: Level) {    when (level) {        OverFlow -> trace(">>> OverFlow!")        Empty -> trace("Alert: Empty!")        else -> trace("Level $level OK")    }}fun main() {    checkLevel(Empty)    checkLevel(Low)    checkLevel(OverFlow)    trace eq """        Alert: Empty!        Level Low OK        >>> OverFlow!    """.trimIndent()}