package listsimport atomicTest.eqfun main() {    val doubles = listOf(1.1, 2.2, 3.3, 4.4)    doubles.sum() eq 11.0    val strings = listOf("Twas", "Brilig", "And", "Slithy", "Toves")    strings eq listOf("Twas", "Brilig", "And", "Slithy", "Toves")    strings.sorted() eq listOf("And", "Brilig", "Slithy", "Toves", "Twas")    // sort =   원본 객체를 직접 정렬    // sorted = 원본 객체를 정렬한 새 List를 새로운 객체에 리턴    strings.reversed() eq listOf("Toves", "Slithy", "And", "Brilig", "Twas")    // reverse =  원본 객체의 원소 순서를 거꾸로 뒤집음    // reversed = 원본 객체를 거꾸로 뒤집은 새 List를 새로운 객체에 리턴    strings.first() eq "Twas"    strings.takeLast(2) eq listOf("Slithy", "Toves")}