package listsimport atomicTest.eqimport atomicTest.neqfun main() {    // 타입 추론을 사용하는 것은 좋은 습관    // 이해하기 쉽게 작성하기 위해선, 타입을 명시해야 함    // <> : 타입 파라미터를 표시    val numbers = listOf(1, 2, 3)    val numbersWithType1: List<Int> = listOf(1, 2, 3)    val numbersWithType2 = listOf<Int>(1, 2, 3)    val strings = listOf("one", "two", "three")    val stringsWithType1: List<String> = listOf("one", "two", "three")    val stringsWithType2 = listOf<String>("one", "two", "three")    numbers eq numbersWithType1    numbers eq numbersWithType2    numbersWithType1 eq numbersWithType2    strings eq stringsWithType1    strings eq stringsWithType2    stringsWithType1 eq stringsWithType2}