package loopingAndRangesfun showRange(r: IntProgression) {  // IntProgression은 Int 범위를 포함하며, 코틀린이 제공하는 기본 타입    for (i in r) {        print("$i ")    }    print(" // $r")    println()}fun main() {    showRange(1..5) // 1 ~ 5    showRange(0 until 5) // 0 ~ 4    showRange(5 downTo 1) // 5 ~ 1    showRange(0..9 step 2) // [0,2,4,6,8]    showRange(0 until 10 step 3) // [0,3,6,9]    showRange(9 downTo 2 step 3) // [9,6,3]}