package manipulatingListsimport atomicTest.eqclass Book(    val title: String,    val authors: List<String>)fun main() {    val books = listOf(        Book("1984", listOf("George Orwell")),        Book("Ulysses", listOf("James Joyce"))    )    books.map { it.authors } eq            listOf(                listOf("George Orwell"),                listOf("James Joyce")            )    books.flatMap { it.authors } eq            listOf("George Orwell", "James Joyce")}