package namedAndDefaultArgsimport atomicTest.eq/** * joinToString() *  : 이터레이션 가능 객체의 원소를 콤마로 구분하여 String 으로 반환 *  1. prefix 접두사 *  2. postfix 접미사 *  3. separator 구분자 */fun main() {    val list = listOf(1, 2, 3)    list.toString() eq "[1, 2, 3]"    list.joinToString() eq "1, 2, 3"    list.joinToString(prefix = "(", postfix = ")") eq "(1, 2, 3)"    list.joinToString(separator = ":") eq "1:2:3"}