package secondaryConstructorsimport atomicTest.eqimport secondaryConstructors.Material.*enum class Material {    Ceramic, Metal, Plastic}class GardenItem(val name: String) {    var material: Material = Plastic    constructor(        name: String, material: Material    // 주생성자만 var, val 가능    ) : this(name) {    // 부생성자에는 리턴 타입 지정 불가        this.material = material    }    constructor(        material: Material    ) : this("Strange Thing", material)    override fun toString() = "$material $name"}fun main() {    GardenItem("Elf").material eq Plastic    GardenItem("Snowman").name eq "Snowman"    GardenItem("Gazing Ball", Metal) eq "Metal Gazing Ball"    GardenItem(material = Ceramic) eq "Ceramic Strange Thing"}