package secondaryConstructorsimport atomicTest.traceclass WithSecondary(i: Int) {    init {        trace("Primary: $i")    }    constructor(c: Char) :            this(c - 'A') {        trace("Secondary: '$c'")    }    constructor(s: String) :            // this 사용 하는 부분은 생성자 로직 앞에 위치해야 한다.            // 생성자 본문이 다른 초기화의 결과에 영향을 받을 수 있기 때문            this(s.first()) {        trace("Secondary: \"$s\"")    }    /* 주 생성자를 호출하지 않으면 컴파일이 되지 않는다.    constructor(f: Float) {        trace("Secondary: $f")    }     */}fun main() {    fun sep() = trace("-".repeat(10))    WithSecondary(1)    sep()    WithSecondary('D')    sep()    WithSecondary("Last Constructor")    trace eq """    Primary: 1    ----------    Primary: 3    Secondary: 'D'    ----------    Primary: 11    Secondary: 'L'    Secondary: "Last Constructor"  """}