package setsimport atomicTest.eqfun main() {    val intSet = setOf(1, 1, 2, 3, 9, 9, 4)    intSet eq setOf(1, 2, 3, 4, 9)    // Set은 순서 상관 없음    setOf(1, 2) eq setOf(2, 1)    // 원소인지 검사    (9 in intSet) eq true    (99 in intSet) eq false    intSet.contains(9) eq true    intSet.contains(99) eq false    // 이 집합이 다른 집합을 포함하는가?    intSet.containsAll(setOf(1, 9, 2)) eq true    // 합집합    intSet.union(setOf(3, 4, 5, 6)) eq            setOf(1, 2, 3, 4, 5, 6, 9)    // 교집합    intSet intersect setOf(0, 1, 2, 7, 8) eq setOf(1, 2)    intSet - setOf(0, 1, 9, 10) eq setOf(2, 3, 4)}