package typeCheckingimport atomicTest.eqinterface BeverageContainer {    fun open(): String    fun close(): String}class Can : BeverageContainer {    override fun open() = "Pop Top"    override fun close() = "Can: Pour"}open class Bottle : BeverageContainer {    override fun open() = "Remove Cap"    override fun close() = "Bottle: Pour"}class GlassBottle : Bottle()class PlasticBottle : Bottle()fun BeverageContainer.recycle() =    when (this) {        is Can -> "Recycle Can"        is GlassBottle -> "Recycle Glass"        else -> "Landfill"    }fun main() {    var refrigerator = listOf(Can(), GlassBottle(), PlasticBottle())    refrigerator.map { it.open() } eq            "[Pop Top, Remove Cap, Remove Cap]"    refrigerator.map { it.recycle() } eq            "[Recycle Can, Recycle Glass, " +            "Landfill]"}