    package visibilityclass Counter(var start: Int) {    fun increment() {        start += 1    }    override fun toString() = start.toString()}class CounterHolder(counter: Counter) {    private val ctr = counter    override fun toString() = "CounterHolder:$ctr"}fun main() {    val c = Counter(11)    val ch = CounterHolder(c)    println(ch)    c.increment()   // CounterHolder 내부에 private val로 정의된 ctr을 여전히 c를 통해 조작이 가능하다.    println(ch)    val ch2 = CounterHolder(Counter(9)) // Counter(9)를 가리키는 참조가 존재하지 않는다.    // ch2 빼곤 이 객체(Counter(9))를 조작할 수 없다.    println(ch2)}